mapper.select:
SELECT count(*) from inven_bbs
WHERE status = 0
AND type = #{reqnum}

SELECT * FROM inven_comment
WHERE b_idx = #{b_idx}
ORDER BY c_idx

SELECT * FROM (SELECT rownum r_num, a.*
				FROM (SELECT * FROM inven_bbs
					  WHERE status = 0
					  and type = #{reqnum}
					  ORDER BY b_idx DESC) a
)
WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE inven_bbs
SET hit = hit+1
WHERE b_idx = #{b_idx}
